-- 总代理子用户数目
SELECT 
       u.user_id user_id
FROM   forum.t_user u
INNER JOIN game.t_group_ref r1
      ON u.user_code = r1.user_id and r1.CRT_TIME<@param1
INNER JOIN game.t_group_ref r2
      ON r1.root_id = r2.ref_id
INNER JOIN forum.t_user u2
      ON r2.user_id = u2.user_code
      AND u2.USER_ID in (select user_id from report.t_user_general_agent t)
		and u.client_id = 'BYAPP'

union  

select user_code from report.t_user_general_agent t;


select u.USER_CODE from forum.t_user u where u.ACCT_NUM='11905967';


select t.REF_ID from game.t_group_ref t where t.USER_ID in (select u.USER_CODE from forum.t_user u where u.ACCT_NUM='11905967');




